import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'my-store';
  btnDisable = true;
  // le estamos dando propiedades a clicl
  Activar(){
    if this.btnDisable == true 
      this.btnDisable=false;
    else
      this.btnDisable=true
  }
}
<h1>Hola mundo</h1>
<!-- template con doble {{}} se llama string interpolation-->
<h2>{{'Hola mundo'.repeat(5) }}</h2>
<!-- aca podemos ver que con los templates son funciones que nos arrojan el resultado -->
<p>3+3 = {{3+3}}</p>

<h1>mi carpeta donde estoy almacenando todo esto es {{title}}</h1>


<h1>Propiedades o atributos</h1>
<!-- cuando utilizamos las [] es para decir que le vamos a pasar un valor desde componentes -->
<button [disabled]="btnDisable">Enviar</button>
<input type="text" [value]="title">

<!-- ahora vamos a ver eventos como por ejemplo click -->
<button [disabled]="btnDisable">Enviar</button>
<button (click)="Activar()">Activar</button>
