import { Component, Input } from '@angular/core';
import{Product} from '../../Reglaprod/prod.component';
import {StoreService} from '../../service/store.service';
@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.scss']
})
export class ProductsComponent {
  myShoppingCart: Product[] = [];
  total=0;

  products: Product[] = [
    {
      id:'1',
      nombre: 'EL mejor juguete',
      precio: 565,
      imagen: '../assets/images/blueberries-g9fcd2bb5c_1280.jpg',
    },
    {
      id:'2',
      nombre: 'Bicicleta casi nueva',
      precio: 356,
      imagen: '../assets/images/carrots-g4d63fab41_1280.jpg'
    },
    {
      id:'3',
      nombre: 'Colleción de albumnes',
      precio: 34,
      imagen: '../assets/images/easter-g682c5c674_1280.jpg'
    },
    {
      id:'4',
      nombre: 'Mis libros',
      precio: 23,
      imagen: '../assets/images/fruits-g5881f0492_1280.jpg'
    },
    {
      id:'5',
      nombre: 'Casa para perro',
      precio: 34,
      imagen: '../assets/images/strawberry-gf204e8e89_1280.jpg'
    },
    {
      id:'6',
      nombre: 'Gafas',
      precio: 3434,
      imagen: '../assets/images/blueberries-g9fcd2bb5c_1280.jpg'
    }
  ]

  onAddtoShoppingCart(product:Product): void{
    this.myShoppingCart.push(product)
    this.total=this.myShoppingCart.reduce((sum,item)=> sum + item.precio, 0);
  }
}
