import { Component ,OnInit} from '@angular/core';
import {StoreService} from '../../service/store.service'
import { AuthService } from 'src/app/service/auth.service';
import {Subscription} from 'rxjs';
import {Product} from '../../Models/prod.models';
@Component({
  selector: 'app-nav',
  templateUrl: './nav.component.html',
  styleUrls: ['./nav.component.scss']
})
export class NavComponent implements OnInit {
  myShoppingCart: Product[] = [];
  seeItemscart:boolean=false;
  usuario:string="";  
  private subscription :Subscription=new Subscription ;
  // profile: User | null = null;
  constructor(
    private storeService:StoreService,
    // private usersService:UsersService,
    private authService:AuthService
  ){}

  activeMenu=false;
  counter=0
  ngOnInit(): void {
    this.storeService.myCart$
      .subscribe( product =>{this.counter = product.length}
      );
    this.subscription = this.authService.getUsuiaro().subscribe(email=>this.usuario=email)
    this.myShoppingCart=this.storeService.getmyShoppingCart()
    }
  ngOnDestroy(): void {
      this.subscription.unsubscribe();
    }
  onItemsCart():void{
    this.seeItemscart = true;
  }
  offItemsCart():void{
    this.seeItemscart = false;
  }

  toggleMenu(): void{
    this.activeMenu=!this.activeMenu;
  }
